mapper.select:
select
  id, dev_id, last_use_time, current_state, loca_id, gw_id
from electricity_consumption.device_status


        and id = #{id}


        and dev_id = #{devId}


        and last_use_time = #{lastUseTime}


        and current_state = #{currentState}


        and loca_id = #{locaId}


        and gw_id = #{gwId}
